const app = Vue.createApp({
    data() {..},

    // Method are delcared along side the data attribute
    methods: {
        outputGoal() {
            const randomNumber = Math.random();
            if (randomNumber < 0.5) {
                return 'Learn Vue!'
            } else {
                return 'Master Vue!'
            }
        }
    }
});

const app = Vue.createApp({
    data() {
        return {
            courseGoalA: 'Finish the Course and learn view',
            courseGoalB: 'Master Vue and create apps',
            vueLink: 'http://www.saez.com/'
        };
    },
    methods: {
        outputGoal() {
            const randomNumber = Math.random();
            if (randomNumber < 0.5) {
                return this.courseGoalA
            } else {
                return this.courseGoalB
            }
        }
    }
});

