let masonryApp = (() => {
  let init = () => {
        // Fire on ready
        $(document).ready(function () {
            // Starts of main chain of functions.
            getData(); // -> createElements-> renderElements-> arrangeElements
            // Stand alone click listener for accordions
            accordion();
        });
    },
    // Get data from asset groups & pass to createElement();
    getData=()=>{
        let url="/getassetgroupjson?guid=";   
        let pageOne= url + "d7e2378c-3007-4a30-83fc-f64a8753cb87"; 
        let pageTwo= url + "18c3a3bc-d811-4fbd-9642-64152c8cd2aa"; 
        // Execute a function after two Ajax requests are successful
        $.when( $.ajax( pageOne ), $.ajax( pageTwo ) ).done(function( d1, d2 ) {
            // d1 and d2 are arguments resolved for the pageOne and pageTwo ajax requests, respectively.
            // Each argument is an array with the following structure: [ data, statusText, jqXHR ]
            let data = [];
            // Add d1[data] + d2[data] to data array
            data.push(...d1[ 0 ],...d2[ 0 ]);
            // Create Elements with data
            createElements(data);
             // Pass data to player
            player.setAssets(data);
        });
    },
    // Create blocks of HTML with data and pass to renderElements();
    createElements=(data)=>{
        let html=''
        for(let i = 0 ; i < data.length; i++) {
            let val = data[i];
            imgSrc = "/?action=asset&x=650&q=50&id="+ val.Guid;
            html +=  '<div class="grid-item" onclick="videoModalClick('+i+');">'
            +               '<div class="inner" style="padding-bottom:' + val.ImageY / val.ImageX * 100 + '%";>'
            +                   '<div class="overlay">'
            +                       '<h4>'+ (val.Name == null ? "" : val.Name).toUpperCase() +'</h4>'
            +                       '<button onclick="videoModalClick('+i+');"class="button ghost small">PLAY TRAILER</button>'
            +                   '</div>'
            +                   '<img class="nofade" src="'+ imgSrc +'"/>'
            +               '</div>'
            +           '</div>'
        }
        // Pass HTML to a new function
        renderElements(html);  
        // Click listener to fire video player
        videoModalClick=(e)=>{
            player.createPlayer(data[e]);
        }
    }
    // Pass HTML blocks to on page container & call arangeElements();
    renderElements=(html)=>{
        // Target container
        document.getElementById('powered-list').insertAdjacentHTML('beforeEnd', html);
        // Target masonry items
        let gridItem = $('.grid-item');
        // Hide all items
        gridItem.hide();
        // Show first 12 items
        gridItem.slice(0, 12).show();
        // Start masonry arrangement
        arrangeElements();
    },
    // Use Packery.js to arrange the items into a masonry style: https://packery.metafizzy.co/
    arrangeElements = () => {
         // Set masonry settings
        let packeryOptions = {
            itemSelector: '.grid-item',
            columnWidth: '.grid-sizer',
            gutter: '.gutter-sizer',
            percentPosition: true,
            horizontalOrder: true,
        };
        // Initialize packery
        $('#powered-list').packery( packeryOptions );
        // Listen to clicks on button
        $("#loadMore").on('click', function (e) {
            e.preventDefault();
            // Target hidden items
            let gridItem = '.grid-item:hidden'
            let container = '#powered-list'
            // Show 4 more hidden items
            $(gridItem).slice(0, 4).fadeIn();
            // Return the element back to its pre-initialized state.
            $(container).packery('destroy');
            // Re-initialize
            $(container).packery( packeryOptions );
            // If there are no more hidden items hide the button
            if ($(gridItem).length == 0) {
                $("#loadMore").fadeOut('slow');
            }
          });
    },
    // Control for team accordions
    accordion=()=>{
        $(".draw").hide();
        $(".trigger").click(function () {
            let $this = $(this);
            $this.parent('.accordion-block').toggleClass('active');
            $this.next().slideToggle( "500","linear");
        });
    }
    ;
  init();
  return {};
})();

