.powered-list .tile{
    width: calc(100% - 1em);
    display: block;
    position: relative;
    padding: 0.4em;
    cursor: pointer;
    pointer-events: all;
    font-size: 0.8em;
}

.powered-list .tile .overlay{
    position: absolute;
    color: #fff;
    width: 100%;
    height: 100%;
    background-color: #000c;
    font-family: 'Lato';
    text-align: center;
    opacity: 0;
    -webkit-transition: opacity 0.5s;
    -o-transition: opacity 0.5s;
    transition: opacity 0.5s;
}

.powered-list .tile .overlay label{
    display: block;
    position: absolute;
    top: 40%;
    left: 50%;
    -webkit-transform: translate(-50%, -50%);
        -ms-transform: translate(-50%, -50%);
            transform: translate(-50%, -50%);
    width: calc(100% - 2em);
    font-weight: 600;
    letter-spacing: 0.2em;
    padding: 0 1em;
}

 .powered-list .tile .overlay button{
    position: absolute;
    left: 50%;
    -webkit-transform: translate(-50%,-50%);
        -ms-transform: translate(-50%,-50%);
            transform: translate(-50%,-50%);
    top: 60%;
    border: 1px solid #fff;
    background: none;
    color: #fff;
    font-family: 'Lato';
    margin: 0;
    outline: 0;
    padding: 0.3em 0.7em 0.4em;
    letter-spacing: 0.2em;
    font-size: 0.8em;
    width: calc(100% - 2em);
    max-width: 12em;
}

.powered-list .tile:hover .overlay{
    opacity: 1;
}

.powered-list .tile .image{
    width: 100%;
    position: relative;
    overflow: hidden;
}

.powered-list .tile .image .inner{
    width:100%;
    height: 100%;
    position: absolute;
    background-size: cover;
    -webkit-transition: all 0.5s;
    -o-transition: all 0.5s;
    transition: all 0.5s;
}

.powered-list .tile:hover .image .inner{
    -webkit-transform: scale(1.05);
        -ms-transform: scale(1.05);
            transform: scale(1.05);
}

.powered-list .cols-1, .powered-list .cols-2, .pix-home .powered-list .cols-3, .powered-list .cols-4, .powered-list .cols-5, .powered-list .cols-6, .powered-list .cols-7{
