let pixHomeSettings = (() => {
    let init = () => {
        $(document).ready(function() {
            if ($('.pix-home').length) {
                panelAnimation(); // panel opacity pinning & text animation
                getPanelImages(); // parse guid to render images for image panels & add CMS data to each
                // OTHER FUNCTIONS CALLED AFTER PANEL IMAGES HAVE ALL LOADED
                scrollControl();
            }
        });
    },

    panelAnimation=()=>{
        let mm = gsap.matchMedia();
        mm.add("(min-width: 1000px)", () => {

            let pinRightPanels = gsap.utils.toArray(".pinRight");
            pinRightPanels.forEach((pinRight, i) => { // right panel - text - get all items to fade in
                ScrollTrigger.create({
                    trigger: pinRight,
                    start: "top top",
                    pin: true,
                    end: "max",
                    pinSpacing:false,
                    id: "pinRight"+i,
                });
            });

            let imgTrig = gsap.utils.toArray(".rightTrigger");
            let panelAnimation = gsap.utils.toArray(".panelAnimation");
            panelAnimation.forEach((rightBlock, i) => { // right panel - text - get all items to fade in
                rightBlock.text = rightBlock.querySelectorAll(".staggerAnimateUp"); // get all child items to stagger in
                const panelTimeline = gsap.timeline({defaults: { ease: "none"}})
                panelTimeline
                    .fromTo(rightBlock,{ autoAlpha:0 },{ duration: 1, autoAlpha: 1}) // fade in panel
                    .fromTo(rightBlock.text,{ y: '60vh', autoAlpha:0 },{ duration: 0.5, autoAlpha:1, y: 0, stagger: 0.2}, ">-0.2"); // move from y 60vh to 0vh staggered
                ScrollTrigger.create({
                    trigger: () => imgTrig[i], // trigger from marker of same index
                    animation: panelTimeline,
                    start: "top top",
                    // end: "bottom bottom",
                    toggleActions: "play none none reverse",

                });
            });

            let pinLeftPanels = gsap.utils.toArray(".pinLeft");
            pinLeftPanels.forEach((panel, i) => { // left panel - image
                ScrollTrigger.create({
                    trigger: panel,
                    start: "top top",
                    end: "+=200%",
                    pin: true,
                    pinSpacing:false,
                    id: "#"+panel.getAttribute("id"), // used for desktop navigation
                });
            });
        });
        mm.add("(max-width: 999px)", () => {

            let pinAllMobilePanels = gsap.utils.toArray(".pinAllMobile");
            pinAllMobilePanels.forEach((panel, i) => {
                ScrollTrigger.create({
                    trigger: panel,
                    end: "max",
                    start: "top top",
                    pin: true,
                    pinSpacing: false,
                    id: "#"+panel.getAttribute("id"), // used for mobile navigation
                });
            });
        });
    }

    images,
    getPanelImages=()=>{ // load images for left hand panels
        $.getJSON("/getassetgroupjson?guid=bb783f99-7606-4dae-8d6d-b1efba266ff7").done(data=>{
            images = data;
            updateImages();
        });
        function updateImages(){
            var rImages = groupBy(randomise(images),e=>e.Name).map(e=> randomise(e.value)[0]);
            let first = 1;
            updateImage=(e,i)=>{
                $e = $(e);
                $e.css("background-image","url(/?action=asset&id=" + rImages[i].Guid + ")");
                if(rImages[i].HotspotX != -1){
                    $e.css("background-position", rImages[i].HotspotX + "% " + rImages[i].HotspotY + "%");
                }
            };

            const imageBlocks = $(".image-block.random").toArray();
            let firstItem=[];
            firstItem.push(imageBlocks[0]);
            renderPanels(firstItem); // call function with first image
            function renderPanels(imageBlocks){
                imageBlocks.forEach(function(value,i){
                    updateImage(value,i);
                    let src = "/?action=asset&id=" + rImages[i].Guid;
                    if(first === 0 && i === imageBlocks.length - 1){ // last loop
                        proxyLoader(value, src, true); // pass true for last
                    }else{
                        proxyLoader(value, src); // load event on image
                    }
                });
            }
            function proxyLoader(image, imgSrc, last){  // control timing of reveal based on load of proxy img
                let imgProxy = document.createElement('img'); // create proxy image
                imgProxy.src = imgSrc; // set src
                imgProxy.onload = function() { // on src load 
                    image.style.opacity = 1 // set wrapper opacity
                    if(first === 1){ // if first being called
                        first = 0; // change param to route on additional runs
                        const newImageBlocks = $(".image-block.random").not('loaded').toArray();
                        renderPanels(newImageBlocks); // call the rest of the images
                    }
                    if(last){ // if first being called
                        addQuotes();
/* TOP LEVEL FUNCTION CALL */ 
                        caseStudySection(); // parse json page data / create slider 
                    }
                };
            }
        }
        function groupBy(list, keyGetter){    
            const map = new Map();
            list.forEach((item) => {
                 const key = keyGetter(item);
                 const collection = map.get(key);
                 if (!collection) {
                     map.set(key, [item]);
                 } else {
                     collection.push(item);
                 }
            });
            return [...map].map((e) => { return {key : e[0], value : e[1]};});
        }
        function randomise(a){
            return a.sort(()=> Math.random() > 0.5 ? 1 : -1);
        }
        function addQuotes(){
            var quotes = randomise($("#quotes tr").toArray().map(row=>{
                return {
                    quote: row.children[0].innerHTML, 
                    source: row.children[1].innerHTML
                };
            }));
            updateQuote=(i)=>{
                return `<div class="quote-block quoteBlock">
                            <div class="inner">
                                <blockquote>${quotes[i].quote}</blockquote>
                                <label>${quotes[i].source}</label>
                            </div>
                            <div class="overlay"></div>
                        </div>
                        `
            };
            let imageBlock = document.querySelectorAll('.quotes');
            imageBlock.forEach((image, index, array) => { // for each item in data batch
                image.insertAdjacentHTML('beforeend',
                    updateQuote(index)
                )
            });
            pinQuotes();
/* TOP LEVEL FUNCTION CALL*/ 
            textTreatment(); // ophans & kerning
        }
        function pinQuotes(){
            let quoteBlocks = gsap.utils.toArray(".quoteBlock");
            quoteBlocks.forEach((quote, i) => {
                quotes.text = quote.querySelectorAll(".inner");
                quotes.overlay = quote.querySelectorAll(".overlay");

                const quotesTl = gsap.timeline({defaults: { ease: "none"}})
                quotesTl
                    .add('start')
                    .fromTo(quotes.text ,{ y: '60vh', autoAlpha:0 },{ duration: 0.8, autoAlpha:1, y: 0}, 'start')
                    .fromTo(quote.overlay, { autoAlpha:0 },{ duration: 1, autoAlpha: 1}, "start")

                ScrollTrigger.create({
                    animation:quotesTl,
                    trigger: quote,
                    start: "bottom bottom",
                    end: "top top-=50%",
                    pin: true,
                    pinSpacing:false,
                    toggleActions: "play none none reverse",
                });
            });
        }
    }

    caseStudySection=()=>{ // load case studies from json page and render on screen
        const folderPaths = $(".page-path").text(); // grab paths to folder pages
        const folderPathsArray = folderPaths.split(','); // add paths to array
        let slideDataArray = []; // array to store card data
        let first = 1;
        requests = folderPathsArray.map((folderPath) => $.getJSON(folderPath,{ // get page data from folder pages
            tagmode: "any",
            format: "json"
        })); 
        Promise.all(requests) // when complete process the data
            .then(processData)
            .catch(err => {
                console.log(err);
            }); 
        function processData(folderData) { // combine data and push to new array for rendering
            let merged=[];
            for(var j = 0; j < folderData.length; j++){
                merged.push(...folderData[j].nodes)
            }
            for (var i = 0; i < merged.length; i++) {
                let customFields = merged[i].customFields; // get custom fields from each page
                let subTitle;
                let image;
                customFields.forEach((field, index, array) => { // data appears in different locations in array so check for name before assigning 
                    if(field.Name ==='Sub Title' && field.Value != ""){ // asign sub title if exists and has text in
                        subTitle = field.Value;
                    }else if(field.Name ==='ogDescription'){ // elseif no subtitle asign ogDescription
                        subTitle = field.Value;
                    }
                    if(field.Name ==='ogImage'){ // asign image
                        image = field.Value;
                    }
                });
                slideDataArray.push( // push data to slide array
                    {
                        title : merged[i].title,
                        path : merged[i].path,
                        kebabTitle : merged[i].name,
                        unixDate: merged[i].date,
                        subTitle : subTitle,
                        image : "/?action=asset&x=2000&q=80&id=" + image,
                    }
                )
            }
            const sortedSlideDataArray = slideDataArray.sort( // sort array by the date property 
                (objA, objB) => Number(objB.unixDate) - Number(objA.unixDate),
            );
            renderSlides(sortedSlideDataArray);
        }
        function renderSlides(sortedSlideDataArray) { // function to show cards
            const slideWrapper = document.getElementById("slides-marker");  // get slides wrapper
            let slideIndex = 0;
            let batchIndex = 1;
            const batchSize = 9;
            const batch = sortedSlideDataArray.map( function(e,i){  // split data in batches
                 return i%batchSize===0 ? sortedSlideDataArray.slice(i,i+batchSize) : null; 
            }).filter(function(e){ return e; });

            let firstImage=[]; // create new array for use with first item
            firstImage.push(batch[0][0]); // push first item to new array
            batch[0].shift(); // remove item from old array
            renderSlideBatch(firstImage); 

            function renderSlideBatch(batchSlideDataArray){ // create slide batches
                batchSlideDataArray.forEach((slide, index, array) => { // for each item in data batch
                    slideWrapper.insertAdjacentHTML('beforeend', // insert the result into the wrapper
                        createSlide( // pass data to slide function
                            slide.title, 
                            slide.path, 
                            slide.kebabTitle, 
                            slide.subTitle, 
                            slide.image, 
                        )
                    );

                    if(first === 0 && index === batchSlideDataArray.length - 1){ // last loop
                        proxyLoader(slide.image, true); // load event on image with last flag
                        ScrollTrigger.create({
                            trigger: ('#case-study-container'),
/* TOP LEVEL FUNCTION CALL*/     
                            onEnter: caseStudySlider,
                        });
                    }else{
                        proxyLoader(slide.image); // load event on image
                    }

                });
            }
            function createSlide(title, path, kebabTitle, subTitle, image) { // create slide function
                slideIndex ++;
                return `
                        <div id="slide-${slideIndex}" class="slide case-study ${kebabTitle}" style="opacity:0";>
                            <a href="${path}"></a>
                            <div class="image-wrapper">
                                <div class="image" style="background: url(${image}) top / cover;"></div>
                            </div>
                            <div class="inner-slide stack">
                                <h3>${title}</h3>
                                <p>${subTitle}</p>
                                <button class="load-case-study" data-page="${kebabTitle}" id="${slideIndex}">READ STORY</button>
                            </div>
                        </div>
                        `
            }
            function proxyLoader(imgSrc, last){  // control timing of reveal base don load of proxy img
                let imgProxy = document.createElement('img'); // create proxy image
                imgProxy.src = imgSrc; // set src
                imgProxy.onload = function() { // on src load 
                    if(first === 0){
                        gsap.set($(".slide"), {autoAlpha: 1})
                    }
                    if(first === 1){ // if first being called
                        first = 0; // change param to route on additional runs
                        gsap.set($("#slide-1"), {autoAlpha: 1}) // if not fading set auto alpha
                        renderSlideBatch(batch[0]); // call the rest of the slides
                    }
                    if(last){ // if last being called
                        caseStudyPageLoader();
/* TOP LEVEL FUNCTION CALL */                              
                        testimonials(); // parse CMS data & create slider aniamtion
                        poweredByPixSection(); // parse guid to create masonry wall
                        trustedSection(); // parse guid to render logo table
                    }
                };
            }
        }
        function caseStudyPageLoader() {
            let button = document.getElementsByClassName('load-case-study');
            let loaderWrapperEl = document.getElementById('loader-container');
            let loaderEl = document.getElementsByClassName('loader-wrapper');
            let el = document.getElementById('article-marker');
            for (var i = 0; i < button.length; i++) {
                button[i].addEventListener('click', function(e){
                    jsonPathFunction(e.currentTarget.getAttribute('data-page'));
                });
            }
            function jsonPathFunction(pagePath) {
                let first = 1;
                el.innerHTML = ''; // clear container
                $(loaderWrapperEl).fadeIn(); // show loader wrapper
                $(loaderEl).fadeIn(); // show loader
                gsap.to(window, {duration: 1.2,  ease: "power4.inOut", scrollTo: {y:el, offsetY: 80}}) // scroll loader into view

                $.getJSON("/getpagejson?path=/pix.online/case-studies/"+pagePath).done(data=>{ // get page data
                    let content = data.customFields[0].Value; // target data
                    // let parseContent = new DOMParser().parseFromString(content, "text/html");
                    var parseContent = document.createElement('div'); // use browser to process in memory
                    parseContent.innerHTML = content // put data in element to process
                    let imgAssets = parseContent.getElementsByClassName('cms-asset'); // target images in data
                    let imgAssetsArray = [...imgAssets]; // change to array
                    imgAssetsArray.forEach((img, index, array) => { // size all images in memory
                        sizeImage(img);
                    });
                    let firstAsset = [] // array for first image
                    firstAsset.push(imgAssetsArray[0]); // add first item
                    loadPageImages(firstAsset); // load images function

                    function loadPageImages(imgDataArray){
                        imgDataArray.forEach((img, index, array) => { // for each item in data batch
                            srcImage(img); // add src
                            proxyLoader(img.dataset.assetid); // load event on image
                            if(first === 0 && index === imgDataArray.length - 1){ // last loop
                                proxyLoader(img.dataset.assetid, true); // load event on image with last flag
                            }
                        });
                    }
                    function sizeImage(imgWrap){
                        let image = imgWrap.childNodes[1]; // target image inside
                        if(first === 0){
                            image.setAttribute('loading','lazy');
                        }
                        // imgWrap.style.opacity = 0;
                        let aspect = parseInt(image.dataset.y) / parseInt(image.dataset.x) * 100; // get size
                        $(image).wrap("<div class=\"asset-wrap\" style=\"padding-bottom:" + aspect +"%;\"><div>"); // wrap image in padding-hac sized div
                    }
                    function srcImage(imgWrap){
                        imgWrap.classList.add('loaded');
                        let image = $(imgWrap).find('img'); // target image inside
                        image[0].src = 'x2x.media/?action=asset&id=' + imgWrap.dataset.assetid;   // set src with URL
                        if(first === 1){ 
                            el.insertAdjacentHTML('beforeend',  parseContent.innerHTML); // insert content to element
                        }
                    }
                    function proxyLoader(imgSrc, last){  // control timing of reveal base don load of proxy img
                        let imgProxy = document.createElement('img'); // create proxy image
                        imgProxy.src = 'x2x.media/?action=asset&id=' + imgSrc; // set src
                        imgProxy.onload = function() { // on src load 
                            if(first === 0){ }
                            if(first === 1){ // if first being called
                                $(loaderEl).fadeOut(); // hide loader
                                first = 0; // change param to route on additional runs
                                var y = $(window).scrollTop();  //your current y position on the page
                                $(window).scrollTop(y+1);
                                let onPageAssets = $('.cms-asset').not('.loaded');
                                let newArray = [...onPageAssets];
                                loadPageImages(newArray);
                            }
                            if(last){ // if last being called
                                inlineVideo(); // fire video code
                                ScrollTrigger.refresh();
                            }
                        };
                    }
                });
            }
            function inlineVideo() {
                const controls = `
                    <div class="plyr__controls">
                       <button type="button" class="plyr__control" data-plyr="fullscreen">
                          <svg class="icon--pressed" role="presentation"><use xlink:href="#plyr-exit-fullscreen"></use></svg>
                          <svg class="icon--not-pressed" role="presentation"><use xlink:href="#plyr-enter-fullscreen"></use></svg>
                          <span class="label--pressed plyr__tooltip" role="tooltip">Exit fullscreen</span>
                          <span class="label--not-pressed plyr__tooltip" role="tooltip">Enter fullscreen</span>
                      </button>
                      <button type="button" class="plyr__control" aria-label="Play, {title}" data-plyr="play">
                          <svg class="icon--pressed" role="presentation"><use xlink:href="#plyr-pause"></use></svg>
                          <svg class="icon--not-pressed" role="presentation"><use xlink:href="#plyr-play"></use></svg>
                          <span class="label--pressed plyr__tooltip" role="tooltip">Pause</span>
                          <span class="label--not-pressed plyr__tooltip" role="tooltip">Play</span>
                      </button>
                      <button type="button" class="plyr__control" aria-label="Mute" data-plyr="mute">
                          <svg class="icon--pressed" role="presentation"><use xlink:href="#plyr-muted"></use></svg>
                          <svg class="icon--not-pressed" role="presentation"><use xlink:href="#plyr-volume"></use></svg>
                          <span class="label--pressed plyr__tooltip" role="tooltip">Unmute</span>
                          <span class="label--not-pressed plyr__tooltip" role="tooltip">Mute</span>
                      </button>
                      <div class="plyr__progress">
                          <input data-plyr="seek" type="range" min="0" max="100" step="0.01" value="0" aria-label="Seek">
                          <progress class="plyr__progress__buffer" min="0" max="100" value="0">% buffered</progress>
                          <span role="tooltip" class="plyr__tooltip">00:00</span>
                      </div>
                      <div class="plyr__time plyr__time--current" aria-label="Current time">00:00</div>
                    </div>
                    `;
                    const players = Array.from(el.querySelectorAll('.cms-video .pure-g .pure-u-1')).map(p => new Plyr(p,{
                        controls: controls,
                        invertTime: true,
                        hideControls: false,
                        disableContextMenu: true
                    }));
            }
        }
        function caseStudySlider(){
            gsap.registerPlugin(Draggable, InertiaPlugin);
            var slideDelay = 5;
            var slideDuration = 0.3;
            var wrap = true;
            var slides = document.querySelectorAll(".slide.case-study");
            var nextButton = document.querySelector("#caseStudyNextButton");
            var progressWrap = gsap.utils.wrap(0, 1);
            var numSlides = slides.length;
            gsap.set(slides, {
                xPercent: i => i * 100
            });
            var wrapX = gsap.utils.wrap(-100, (numSlides - 1) * 100);
            var timer = gsap.delayedCall(slideDelay, autoPlay);
            var animation = gsap.to(slides, {
                xPercent: "+=" + (numSlides * 100),
                duration: 1,
                ease: "none",
                paused: true,
                repeat: -1,
                modifiers: {
                    xPercent: wrapX
                }
            });
            var proxy = document.createElement("div");
            var slideAnimation = gsap.to({}, {});
            var slideWidth = 0;
            var wrapWidth = 0;
            var draggable = new Draggable(proxy, {
                trigger: ".case-study-container",
                inertia: true,
                onPress: updateDraggable,
                onDrag: updateProgress,
                onThrowUpdate: updateProgress,
                type:"x",
                snap: {
                    x: snapX
                }
            });
            resize();
            window.addEventListener("resize", resize);
            nextButton.addEventListener("click", function() {
                animateSlides(-1);
            });
            function updateDraggable() {
                timer.restart(true);
                slideAnimation.kill();
                this.update();
            }
            function animateSlides(direction) {
                timer.restart(true);
                slideAnimation.kill();
                var x = snapX(gsap.getProperty(proxy, "x") + direction * slideWidth);
                slideAnimation = gsap.to(proxy, {
                    x: x,
                    duration: slideDuration,
                    onUpdate: updateProgress
                });
            }
            function autoPlay() {
                if (draggable.isPressed || draggable.isDragging || draggable.isThrowing) {
                    timer.restart(true);
                } else {
                    animateSlides(-1);
                }
            }
            function updateProgress() {
                animation.progress(progressWrap(gsap.getProperty(proxy, "x") / wrapWidth));
            }
            function snapX(value) {
                let snapped = gsap.utils.snap(slideWidth, value);
                return wrap ? snapped : gsap.utils.clamp(-slideWidth * (numSlides - 1), 0, snapped);
            }
            function resize() {
                var norm = (gsap.getProperty(proxy, "x") / wrapWidth) || 0;
                slideWidth = slides[0].offsetWidth;
                wrapWidth = slideWidth * numSlides;
                wrap || draggable.applyBounds({minX: -slideWidth * (numSlides - 1), maxX: 0});
                gsap.set(proxy, {
                  x: norm * wrapWidth
                });
                animateSlides(0);
                slideAnimation.progress(1);
            }
            // pause slider when expanded
            gsap.utils.toArray(".load-case-study").forEach(function(a) {
                a.addEventListener("click", function(e) {
                    timer.kill();
                });
            });
        }
    }

    testimonials=()=>{
        var testimonialsArray=[];
        const testimonialWrapper = document.getElementById("testimonial-marker");  // get slides wrapper
        var testimonial = $("#testimonials tr").toArray().map(row=>{
            let thumbId = row.children[2].children[0].attributes[1].value;
            testimonialsArray.push( // create array of objects from details in the asset group
                {
                    quote: row.children[0].innerHTML, 
                    source: row.children[1].innerHTML,
                    image: thumbId
                }
            )
        });
        testimonialsArray.forEach((quote, index, array) => { // for each item in data batch
            testimonialWrapper.insertAdjacentHTML('beforeend', // insert the result into the card wrapper
                createQuote( // pass data to slide function
                    quote.quote, 
                    quote.source, 
                    quote.image, 
                )
            );
        });
        testimonialSlider();
        function createQuote(quote, source, image) { // create slide function
            return `
                <div class="slide testimonial">
                    <div class="inner">
                       <img class="profile-thumbnail noFade" src="/?action=asset&x=200&q=80&id=${image}">
                       <blockquote>
                         <p>${quote}</p>
                         <p class="quote">${source}</p>
                       </blockquote>
                     </div>
                </div>
                `
        }
        function testimonialSlider(){
            gsap.registerPlugin(Draggable, InertiaPlugin);
            var slideDelay = 5;
            var slideDuration = 0.3;
            var wrap = true;
            var slides = document.querySelectorAll(".slide.testimonial");
            var progressWrap = gsap.utils.wrap(0, 1);
            var numSlides = slides.length;
            gsap.set(slides, {
                xPercent: i => i * 100
            });
            var wrapX = gsap.utils.wrap(-100, (numSlides - 1) * 100);
