            var animation = gsap.to(slides, {
                xPercent: "+=" + (numSlides * 100),
                duration: 1,
                ease: "none",
                paused: true,
                repeat: -1,
                modifiers: {
                    xPercent: wrapX
                }
            });
            var proxy = document.createElement("div");
            var slideAnimation = gsap.to({}, {});
            var slideWidth = 0;
            var wrapWidth = 0;
            var draggable = new Draggable(proxy, {
                trigger: ".testimonials-container",
                inertia: true,
                onPress: updateDraggable,
                onDrag: updateProgress,
                onThrowUpdate: updateProgress,
                type:"x",
                snap: {
                    x: snapX
                }
            });
            resize();
            window.addEventListener("resize", resize);
            function updateDraggable() {
                timer.restart(true);
                slideAnimation.kill();
                this.update();
            }
            function animateSlides(direction) {
                timer.restart(true);
                slideAnimation.kill();
                var x = snapX(gsap.getProperty(proxy, "x") + direction * slideWidth);
                slideAnimation = gsap.to(proxy, {
                    x: x,
                    duration: slideDuration,
                    onUpdate: updateProgress
                });
            }
            function autoPlay() {
                if (draggable.isPressed || draggable.isDragging || draggable.isThrowing) {
                  timer.restart(true);
                } else {
                  animateSlides(-1);
                }
              }
            function updateProgress() {
                animation.progress(progressWrap(gsap.getProperty(proxy, "x") / wrapWidth));
              }
            function snapX(value) {
                let snapped = gsap.utils.snap(slideWidth, value);
                return wrap ? snapped : gsap.utils.clamp(-slideWidth * (numSlides - 1), 0, snapped);
            }
            function resize() {
                var norm = (gsap.getProperty(proxy, "x") / wrapWidth) || 0;
                slideWidth = slides[0].offsetWidth;
                wrapWidth = slideWidth * numSlides;
                wrap || draggable.applyBounds({minX: -slideWidth * (numSlides - 1), maxX: 0});
                gsap.set(proxy, {
                    x: norm * wrapWidth
                });
                animateSlides(0);
                slideAnimation.progress(1);
            }
        }
    }

    poweredByPixSection=()=>{
        var guid="d7e2378c-3007-4a30-83fc-f64a8753cb87",
        data,
        pageSize = 100,
        pages = 1,
        startRows = 4,
        getData=()=>{
            $.get("/getassetgroupjson?guid=" + guid).done(d=>{
                data=d;
                player.setAssets(data);
                setTimeout(()=>{player.initAutoplay();},1500);
                renderList(startRows);
            });
        },
        renderList=(maxRows)=>{
            $container = $(".powered-list");
            $container.html("");
            $container.append(getColumnsHtml(maxRows));
            attachHandlers();
            ScrollTrigger.refresh();
        },
        getColumnsHtml=(maxRows)=>{
            html = "";
            for(c=3;c<=7;c++){
                html+="<div class=\"cols-" + c +"\">";
                html+=getListHtml(c,maxRows);
                html+="</div>";
            }
            if(maxRows!=null){
                html += "<div class=\"next\"></div>";
            }
            return html;
        },
        getListHtml=(columns,maxRows)=>{
            var domains=["codex.online","x2x.media","pix.online"];
            var html = "";
            for(col=0; col<columns; col++){
                width = ((1/columns)*100).toFixed(5);
                html += "<div class=\"column\" style=\"width: " + width + "%\">";
                for(i=col; i<pages * pageSize && (maxRows == null || i<(columns*maxRows)) && i<data.length; i+=columns){
                    item = data[i];
                    html += "<div class=\"tile\" data-id=\"" + i + "\">"
                        + "<div>"
                            + "<div class=\"image\" style=\"padding-bottom:" + item.ImageY / item.ImageX * 100 + "%;\">"
                                + "<div class=\"inner\" style=\"background-image:url(https://" + domains[i%domains.length] + "/?action=asset&x=650&q=50&id=" + item.Guid + ")\"></div>"
                                + "<div class=\"overlay\"><label>" + (item.Name == null ? "" : item.Name).toUpperCase() + "</label><button>PLAY TRAILER</button></div>"
                            + "</div>"
                        +"</div>"
                    + "</div>";
                }
                html += "</div>"
            }
            return html;
        },
        attachHandlers=()=>{
            $(".tile", $container).click(tileClickHandler);
            $(".next", $container).click(showNext);
        },
        tileClickHandler=(e)=>{
            id=$(e.currentTarget,$container).data().id;
            player.createPlayer(data[id]);
        },
        showNext=()=>{
            renderList();
            // ScrollTrigger.refresh();
        }
        ;
        getData();
    }

    trustedSection=()=>{
        var guid="2a713c17-11a5-4c78-b5e0-0fbf0d1e23b9",
        data,
        getData=()=>{
            $.get("/getassetgroupjson?guid=" + guid).done(d=>{
                data=d;
                setTimeout(()=>{player.initAutoplay();},1500);
                renderList();
            });
        },
        renderList=()=>{
            $container = $(".tiles");
            $container.append(getTiles());
        },
        getTiles=(columns)=>{
            var domains=["codex.online","x2x.media","pix.online"];
            var html = "";
            data.forEach((a,i)=>{
                html += "<div class=\"tile\">"
                    + "<div class=\"image\">"
                        + "<div class=\"inner\" style=\"background-image:url(https://" + domains[i%domains.length] + "/?action=asset&id=" + a.Guid + ");"
                        + ((a.Var1 !=null || a.Var2 != null) ? " transform: " : "")
                        + (a.Var2 == null ? " translate(-50%, -50%)" : (" translate(" + a.Var2 + ")"))
                        + (a.Var1 == null ? " scale(100%)" : (" scale(" + a.Var1 + ")"))
                        + ";\">"
                        + "</div>"
                    + "</div>"
                + "</div>";
            });
            return html;
        }
        ;
        getData();
    }

    scrollControl=()=>{
        function backToTop(){
            $(".backtotop").click(function(){
                gsap.to(window, {scrollTo : 0,duration: 1,behavior: 'smooth'});
            });
        }
        function externalScroll(){
            window.onload = (event) => {
                let urlHash = window.location.href.split("#")[1];
                let scrollElem = document.querySelector("#" + urlHash);
                const trigger = ScrollTrigger.getById(scrollElem);

                if (urlHash && scrollElem) {
                   gsap.to(window, {
                      duration: 1,
                      scrollTo: trigger ? trigger.start : scrollElem,
                      behavior: 'smooth'
                    });

                }
            };
        }
        backToTop();
        externalScroll();
    }

    textTreatment=()=>{
        function kernText(){ // split text to target with styles
            $(".kernText").each((i,e)=>{
                split = new SplitText(e, {type: "word,chars"});
                split.chars.forEach(e=>{
                    $(e).attr("data-char",e.innerText);
                });
            });
        }
        function orphanPreventor(){ // add non breaking space between the last words in text blocks
            $('.wrapper h1,.wrapper h2,.wrapper h3,.wrapper h4,.wrapper p, blockquote').each(function(i,d) {
                  $(d).html( $(d).text().replace(/\s(?=[^\s]*$)/g, "&nbsp;") );
            });
        }
        orphanPreventor();
        kernText();
    }
    ;
    init();
    return {};
})();

